CREATE PROCEDURE [dbo].[AddCollection]
	@name VARCHAR(50),
	@description VARCHAR(255)
AS

	SET XACT_ABORT ON;
	SET NOCOUNT ON;
	BEGIN TRY
		BEGIN TRANSACTION
			DECLARE @fid [int];
			DECLARE @pid [int];
			SET @fid =	(SELECT	TOP 1 sampling_feature_id 
						 FROM	SAMPLING_FEATURE
						 WHERE	feature_namespace = @feature_namespace
							AND feature_identifier = @feature_identifier);
			SET @pid = (SELECT TOP 1 generic_property_id 
						FROM GENERIC_PROPERTY
						WHERE property_name = @property);
			INSERT INTO [FEATURE_PROPERTY] ([sampling_feature_id], [generic_property_id], property_value) VALUES (@fid, @pid, @value);
		COMMIT TRANSACTION
	END TRY
	BEGIN CATCH
		IF @@TRANCOUNT > 0
    		ROLLBACK TRANSACTION;

		DECLARE
    		@ERROR_SEVERITY INT,
    		@ERROR_STATE	INT,
    		@ERROR_NUMBER	INT,
    		@ERROR_LINE		INT,
    		@ERROR_MESSAGE	NVARCHAR(4000);

		SELECT
    		@ERROR_SEVERITY = ERROR_SEVERITY(),
    		@ERROR_STATE	= ERROR_STATE(),
    		@ERROR_NUMBER	= ERROR_NUMBER(),
    		@ERROR_LINE		= ERROR_LINE(),
    		@ERROR_MESSAGE	= ERROR_MESSAGE();

		RAISERROR('Msg %d, Line %d, :%s',
    		@ERROR_SEVERITY,
    		@ERROR_STATE,
    		@ERROR_NUMBER,
    		@ERROR_LINE,
    		@ERROR_MESSAGE);;
	END CATCH

CREATE PROCEDURE [dbo].[AddFeatureProperty]
	@feature_namespace VARCHAR(50),
	@feature_identifier VARCHAR(50),
	@property VARCHAR(50),
	@value VARCHAR(200)
AS

	SET NOCOUNT ON;
	SET XACT_ABORT ON;
	BEGIN TRY
		BEGIN TRANSACTION
			DECLARE @id [int];
			INSERT INTO [SAMPLING_FEATURE] (feature_namespace, feature_identifier, feature_type, feature_description) 
				VALUES (@namespace, @identifier, 'Location', @description);
			SELECT @id = SCOPE_IDENTITY();

			IF @latitude IS NOT NULL AND @longitude IS NOT NULL
				INSERT INTO [LOCATION] (sampling_feature_id, location_name, sampled_feature_type, location_type, geographic_feature) 
					VALUES (@id, @name, @sampled_feature_type, @location_type, geography::Point(@latitude, @longitude, @srid));
			ELSE IF @wkt IS NOT NULL
				INSERT INTO [LOCATION] (sampling_feature_id, location_name, sampled_feature_type, location_type, geographic_feature) 
					VALUES (@id, @name, @sampled_feature_type, @location_type, geography::STGeomFromText(@wkt, @srid));
			ELSE
				INSERT INTO [LOCATION] (sampling_feature_id, location_name, sampled_feature_type, location_type) 
					VALUES (@id, @name, @sampled_feature_type, @location_type);

			IF @ancestor_feature IS NOT NULL
				EXEC AddSamplingFeatureRelation @namespace, @ancestor_feature, @namespace, @identifier, 'Parent Location - Sublocation'
		COMMIT TRANSACTION
	END TRY
	BEGIN CATCH
		IF @@TRANCOUNT > 0
    		ROLLBACK TRANSACTION;

		DECLARE
    		@ERROR_SEVERITY INT,
    		@ERROR_STATE	INT,
    		@ERROR_NUMBER	INT,
    		@ERROR_LINE		INT,
    		@ERROR_MESSAGE	NVARCHAR(4000);

		SELECT
    		@ERROR_SEVERITY = ERROR_SEVERITY(),
    		@ERROR_STATE	= ERROR_STATE(),
    		@ERROR_NUMBER	= ERROR_NUMBER(),
    		@ERROR_LINE		= ERROR_LINE(),
    		@ERROR_MESSAGE	= ERROR_MESSAGE();

		RAISERROR('Msg %d, Line %d, :%s',
    		@ERROR_SEVERITY,
    		@ERROR_STATE,
    		@ERROR_NUMBER,
    		@ERROR_LINE,
    		@ERROR_MESSAGE);;
	END CATCH

CREATE PROCEDURE [dbo].[AddLocation]
	@namespace  varchar(50),
	@identifier varchar(50),
	@description varchar(MAX),
	@name varchar(255),
	@sampled_feature_type varchar(50),
	@location_type varchar(50),
	@latitude float = NULL,
	@longitude float = NULL,
	@srid int,
	@wkt varchar(1000) = NULL,
	@ancestor_feature varchar(50) = NULL
AS

	SET NOCOUNT ON;
	SET XACT_ABORT ON;
	BEGIN TRY
		BEGIN TRANSACTION
			DECLARE
				@pid [INT],
				@scid [INT];
			IF @parent_phenomenon IS NOT NULL
				BEGIN
					SET @pid =	(SELECT TOP 1 phenomenon_id
								 FROM PHENOMENON
								 WHERE phenomenon_code = @parent_phenomenon);
					IF @pid IS NULL
						RAISERROR('A matching phenomenon for parent phenomenon code (%s) was not found.', 11, 1, @parent_phenomenon)
				END
			IF @classification_scheme IS NOT NULL
				BEGIN
					SET @scid =	(SELECT TOP 1 classification_scheme_id 
								 FROM CLASSIFICATION_SCHEME
								 WHERE scheme_name = @classification_scheme);
					IF @scid IS NULL
						RAISERROR('A matching classification scheme for (%s) was not found.', 11, 1, @classification_scheme)
				END
			INSERT INTO PHENOMENON ([phenomenon_code],[phenomenon_name],[phenomenon_description],[quantity],[component],[state],[medium],[representation],[parent_phenomenon_id],[result_type], [classification_scheme_id]) 
				VALUES (@code, @name, @description, @quantity, @component, @state, @medium, @representation, @pid, @result_type, @scid);
		COMMIT TRANSACTION
	END TRY
	BEGIN CATCH
		IF @@TRANCOUNT > 0
    		ROLLBACK TRANSACTION;

		DECLARE
    		@ERROR_SEVERITY INT,
    		@ERROR_STATE	INT,
    		@ERROR_NUMBER	INT,
    		@ERROR_LINE		INT,
    		@ERROR_MESSAGE	NVARCHAR(4000);

		SELECT
    		@ERROR_SEVERITY = ERROR_SEVERITY(),
    		@ERROR_STATE	= ERROR_STATE(),
    		@ERROR_NUMBER	= ERROR_NUMBER(),
    		@ERROR_LINE		= ERROR_LINE(),
    		@ERROR_MESSAGE	= ERROR_MESSAGE();

		RAISERROR('Msg %d, Line %d, :%s',
    		@ERROR_SEVERITY,
    		@ERROR_STATE,
    		@ERROR_NUMBER,
    		@ERROR_LINE,
    		@ERROR_MESSAGE);;
	END CATCH

