CREATE PROCEDURE [dbo].[AddRelationship]
	@name VARCHAR(30),
	@description VARCHAR(500)
AS

	BEGIN TRY
		BEGIN TRANSACTION
			SET NOCOUNT ON;
			SET XACT_ABORT ON;

			DECLARE 
				@scid INT,
				@tid  INT;
			SET @scid =	(SELECT TOP 1 [dbo].[CLASSIFICATION_SCHEME].[classification_scheme_id] 
							FROM [dbo].[CLASSIFICATION_SCHEME] 
							WHERE [dbo].[CLASSIFICATION_SCHEME].[scheme_name] = @scheme);
			IF @scid IS NULL
				RAISERROR('A matching classification scheme for @scheme (%s) was not found.', 11, 1, @scheme)
			IF @taxon IS NOT NULL
				BEGIN
					SET @tid =	(SELECT TOP 1 [dbo].[TAXON].[taxon_id] 
									FROM [dbo].[TAXON]
									WHERE [dbo].[TAXON].[taxon_code] = @taxon);
					IF @tid IS NULL
						RAISERROR('A matching taxon for @taxon (%s) was not found.', 11, 1, @taxon)
				END
			INSERT INTO	[dbo].[RESULT_CATEGORY]
				VALUES (@code, @description, @scid, @tid);
		COMMIT TRANSACTION
	END TRY
	BEGIN CATCH
		IF @@TRANCOUNT > 0
    		ROLLBACK TRANSACTION;

		DECLARE
    		@ERROR_SEVERITY INT,
    		@ERROR_STATE	INT,
    		@ERROR_NUMBER	INT,
    		@ERROR_LINE		INT,
    		@ERROR_MESSAGE	NVARCHAR(4000);

		SELECT
    		@ERROR_SEVERITY = ERROR_SEVERITY(),
    		@ERROR_STATE	= ERROR_STATE(),
    		@ERROR_NUMBER	= ERROR_NUMBER(),
    		@ERROR_LINE		= ERROR_LINE(),
    		@ERROR_MESSAGE	= ERROR_MESSAGE();

		RAISERROR('Msg %d, Line %d, :%s',
    		@ERROR_SEVERITY,
    		@ERROR_STATE,
    		@ERROR_NUMBER,
    		@ERROR_LINE,
    		@ERROR_MESSAGE);;
	END CATCH

CREATE PROCEDURE [dbo].[AddResultCategory]
	@code VARCHAR(50),
	@description VARCHAR(50),
	@scheme VARCHAR(50),
	@taxon VARCHAR(150) = NULL
AS

	SET NOCOUNT ON;
	SET XACT_ABORT ON;
	BEGIN TRY
		BEGIN TRANSACTION;
			DECLARE @id [INT];
			DECLARE @procid [INT];
			DECLARE @handid [INT];

			SET @procid =	(SELECT TOP 1 process_id 
							 FROM PROCESS 
							 WHERE process_name = @collection_procedure);
			IF @sample_handling_procedure IS NOT NULL
				BEGIN
					SET @handid =	(SELECT TOP 1 process_id 
									 FROM PROCESS 
									 WHERE process_name = @sample_handling_procedure);
					IF @handid IS NULL
						RAISERROR('A matching process for @sample_handling_procedure (%s) was not found.', 11, 1, @sample_handling_procedure);
				END

			INSERT INTO [SAMPLING_FEATURE] (feature_namespace, feature_identifier, feature_type, feature_description) 
				VALUES (@namespace, @identifier, 'Sample', @description);
			SELECT @id = SCOPE_IDENTITY();
			INSERT INTO [SAMPLE] (sampling_feature_id, media, sample_type, collection_date, [collection_process_id], [handling_process_id], laboratory_identifier, is_qc, is_compliance) 
				VALUES (@id, @media, @sample_type, @collection_date, @procid, @handid, @laboratory_identifier, @is_qc, @is_compliance);
			IF @location_namespace IS NOT NULL AND @location_namespace IS NOT NULL
				EXEC AddSamplingFeatureRelation @location_namespace, @location_identifier, @namespace, @identifier, 'Location - Sample';
			IF @duplicate_identifier IS NOT NULL
				EXEC AddSamplingFeatureRelation @namespace, @identifier, @namespace, @duplicate_identifier, 'Sample - Field Duplicate';
			IF @trip_blank_identifier IS NOT NULL
				EXEC AddSamplingFeatureRelation @namespace, @identifier, @namespace, @trip_blank_identifier, 'Sample - Trip Blank';
			IF @composite_identifier IS NOT NULL
				EXEC AddSamplingFeatureRelation @namespace, @identifier, @namespace, @composite_identifier, 'Sample - Composite Sample';
		COMMIT TRANSACTION;
	END TRY
	BEGIN CATCH
		IF @@TRANCOUNT > 0
    		ROLLBACK TRANSACTION;

		DECLARE
    		@ERROR_SEVERITY INT,
    		@ERROR_STATE	INT,
    		@ERROR_NUMBER	INT,
    		@ERROR_LINE		INT,
    		@ERROR_MESSAGE	NVARCHAR(4000);

		SELECT
    		@ERROR_SEVERITY = ERROR_SEVERITY(),
    		@ERROR_STATE	= ERROR_STATE(),
    		@ERROR_NUMBER	= ERROR_NUMBER(),
    		@ERROR_LINE		= ERROR_LINE(),
    		@ERROR_MESSAGE	= ERROR_MESSAGE();

		RAISERROR('Msg %d, Line %d, :%s',
    		@ERROR_SEVERITY,
    		@ERROR_STATE,
    		@ERROR_NUMBER,
    		@ERROR_LINE,
    		@ERROR_MESSAGE);
	END CATCH;

CREATE PROCEDURE [dbo].[AddSample]
	@namespace VARCHAR(50),
	@identifier VARCHAR(50),
	@description VARCHAR(MAX),
	@media VARCHAR(50),
	@sample_type VARCHAR(100),
	@collection_date DATETIME,
	@collection_procedure VARCHAR(200),
	@sample_handling_procedure VARCHAR(200) = NULL,
	@laboratory_identifier VARCHAR(50),
	@is_qc BIT,
	@is_compliance BIT,
	@location_namespace VARCHAR(50) = NULL,
	@location_identifier VARCHAR(50) = NULL,
	@duplicate_identifier VARCHAR(50) = NULL,
	@trip_blank_identifier VARCHAR(50) = NULL,
	@composite_identifier VARCHAR(50) = NULL
AS

	SET NOCOUNT ON;
	SET XACT_ABORT ON;
	BEGIN TRY
		BEGIN TRANSACTION;
			DECLARE @fid [INT];
			DECLARE @tid [INT];
			DECLARE @relid [INT];
			SET @fid =	(SELECT TOP 1 sampling_feature_id 
							FROM SAMPLING_FEATURE
							WHERE feature_namespace = @from_namespace AND feature_identifier = @from_identifier);
			SET @tid =	(SELECT TOP 1 sampling_feature_id 
							FROM SAMPLING_FEATURE
							WHERE feature_namespace = @to_namespace AND feature_identifier = @to_identifier);
			SET @relid =	(SELECT TOP 1 relationship_id
								FROM RELATIONSHIP
								WHERE relationship_name = @relation_name);
			IF @fid IS NULL
				RAISERROR('A matching sampling feature for (%s %s) was not found.', 11, 1, @from_namespace, @from_identifier)
			IF @tid IS NULL
				RAISERROR('A matching sampling feature for (%s %s) was not found.', 11, 1, @to_namespace, @to_identifier)
			IF @relid IS NULL
				RAISERROR('A relationship_id was not found for ''%s'' relation: make sure that the relation you''re attempting to use exists in the database.', 11, 1, @relation_name)
			INSERT INTO [SAMPLING_FEATURE_RELATION] ([from_sampling_feature_id], [to_sampling_feature_id], relationship_id) VALUES (@fid, @tid, @relid);
		COMMIT TRANSACTION
	END TRY
	BEGIN CATCH
		IF @@TRANCOUNT > 0
    		ROLLBACK TRANSACTION;

		DECLARE
    		@ERROR_SEVERITY INT,
    		@ERROR_STATE	INT,
    		@ERROR_NUMBER	INT,
    		@ERROR_LINE		INT,
    		@ERROR_MESSAGE	NVARCHAR(4000);

		SELECT
    		@ERROR_SEVERITY = ERROR_SEVERITY(),
    		@ERROR_STATE	= ERROR_STATE(),
    		@ERROR_NUMBER	= ERROR_NUMBER(),
    		@ERROR_LINE		= ERROR_LINE(),
    		@ERROR_MESSAGE	= ERROR_MESSAGE();

		RAISERROR('Msg %d, Line %d, :%s',
    		@ERROR_SEVERITY,
    		@ERROR_STATE,
    		@ERROR_NUMBER,
    		@ERROR_LINE,
    		@ERROR_MESSAGE);
	END CATCH

